(function () {
  //初始化html字体大小
   function remReSize() {
     var w = window.innerWidth;
     if (w > 640) {
       w = 640;
     };
     document.getElementsByTagName("html")[0].setAttribute("style", "font-size:"+100 / 750 * w + 'px'+"");
   };
   remReSize();
  //当窗体大小变化时，html字体大小随着变化
   window.onresize=function(){
     remReSize();
   };
   window.onload=function () {
     remReSize();
   };
     //避免页面js延迟加载
   for (var i = 0; i < 3; i++) {
     setTimeout(remReSize, 100 * i);
   };
 })();
 